        private List<Color> Load()
        {
            Bitmap file = new Bitmap(Image.FromFile(FilePath));                        
            int offset = 0;
            Color[,] image = new Color[file.Width, file.Height];
            var palette = new List<Color>();

            for (int y = 0; y < file.Height; y++) {
                for (int x = 0; x < file.Width; x++) {
                    image[x,y] = file.GetPixel(x, y);
                }
            }

            // loop through each palette color in the
            // source bmp and index them in order
            // left to right, top to bottom (16x16)
            for (int y = 1; y <= 16; y++) {
                for (int x = 1; x <= 16; x++) {
                    // offset 1 px from left, colors are 6 px wide
                    int cX = 1 + x * 6;

                    // offset 15 px from top, rows are 5 px tall
                    int cY = 15 + y * 5;

                    palette.Add(image[cX,cY]);
                }
            }

            return palette;

        }

        public Color GetColor(SC2PaletteColor index, int frame) {
            if (index.Transparent) return Color.Transparent;
            return palette[index.Color][frame];
        }

